<?php
namespace LoftOcean\Elementor;
/**
 * Elementor Widget Vertical Divider
 */
class Widget_Vertical_Divider extends \LoftOcean\Elementor_Widget_Base {
	/**
	 * Get widget name.
	 * @access public
	 *
	 * @return string Widget name.
	 */
	public function get_name() {
		return apply_filters( 'loftocean_elementor_widget_name', 'loftoceanverticaldivider', array( 'id' => 'vertical-divider' ) );
	}
	/**
	 * Get widget title.
	 * @access public
	 * @return string Widget title.
	 */
	public function get_title() {
		return esc_html__( 'Vertical Divider', 'loftocean' );
	}
	/**
	 * Get widget icon.
	 * @access public
	 *
	 * @return string Widget icon.
	 */
	public function get_icon() {
		return 'eicon-divider';
	}
	/**
	 * Get widget categories.
	 * @access public
	 *
	 * @return array Widget categories.
	 */
	public function get_categories() {
		return array( 'loftocean-theme-category' );
	}
	/**
	 * Get widget keywords.
	 * Retrieve the list of keywords the widget belongs to.
	 * @return array Widget keywords.
	 */
	public function get_keywords() {
		return array( 'divider', 'vertical divider', 'vertical' );
	}
	/**
	* Get JavaScript dependency to render this widget
	* @return array of script handler
	*/
	public function get_script_depends() {
		return array();
	}
	/**
	* Get style dependency to render this widget
	* @return array of style handler
	*/
	public function get_style_depends() {
		return array();
	}
	/**
	 * Register widget controls.
	 * Adds different input fields to allow the user to change and customize the widget settings.
	 * @access protected
	 */
	protected function register_controls() {
		$this->start_controls_section( 'general_content_section', array(
			'label' => __( 'General', 'loftocean' ),
			'tab' => \Elementor\Controls_Manager::TAB_CONTENT
		) );
        $this->add_control( 'type', array(
			'label' => esc_html__( 'Divider Type', 'loftocean' ),
			'type' => \Elementor\Controls_Manager::SELECT,
			'options' => array(
                '' => esc_html__( 'Simple', 'loftocean' ),
                'divider-grass' => esc_html__( 'Grass', 'loftocean' ),
                'divider-star' => esc_html__( 'Star', 'loftocean' )
            )
		) );
        $this->add_control( 'height', array(
			'label' => esc_html__( 'Height', 'loftocean' ),
			'type' => \Elementor\Controls_Manager::SELECT,
			'prefix_class' => '',
			'options' => array(
                '' => esc_html__( 'Default', 'loftocean' ),
                'full-height' => esc_html__( 'Fit To Column', 'loftocean' ),
                'custom-height' => esc_html__( 'Custom', 'loftocean' )
            )
		) );
		$this->add_responsive_control( 'custom_height', array(
			'label' => esc_html__( 'Custom Height', 'loftocean' ),
			'type' => \Elementor\Controls_Manager::SLIDER,
			'size_units' => array( 'px', 'vh' ),
			'range' => array(
				'px' => array( 'max' => 1000, 'min' => 1 ),
				'vh' => array( 'max' => 100, 'min' => 1 )
			),
			'render_type' => 'ui',
			'separator' => 'before',
			'selectors' => array( '{{WRAPPER}} .cs-vertical-divider' => 'height: {{SIZE}}{{UNIT}};' ),
            'condition' => array(
                'height[value]' => 'custom-height'
            )
		) );
        $this->end_controls_section();

        $this->start_controls_section( 'style_section', array(
            'label' => esc_html__( 'Style', 'loftocean' ),
            'tab' => \Elementor\Controls_Manager::TAB_STYLE,
        ) );
		$this->add_control( 'line_color', array(
			'label' => esc_html__( 'Line Color', 'loftocean' ),
			'type' => \Elementor\Controls_Manager::COLOR,
			'default' => '',
			'selectors' => array(
				'{{WRAPPER}} .cs-vd-line' => 'color: {{VALUE}};',
			)
		) );
		$this->add_control( 'icon_color', array(
			'label' => esc_html__( 'Icon Color', 'loftocean' ),
			'type' => \Elementor\Controls_Manager::COLOR,
			'default' => '',
			'selectors' => array(
				'{{WRAPPER}} .cs-vd-icon' => 'color: {{VALUE}};',
			)
		) );
		$this->end_controls_section();
	}
